import React, { useEffect, useState } from "react";
import { Modal, Button, Form } from "react-bootstrap";

const AddEditStockModal = ({ show, handleClose, handleSave, initial, products }) => {
  const [form, setForm] = useState({
    product_ID: "",
    quantity: "",
    last_RestockDate: "",
  });

  const [error, setError] = useState(false);

  useEffect(() => {
    if (initial) {
      setForm({
        product_ID: initial.product_ID || "",
        quantity: initial.quantity || "",
        last_RestockDate: initial.last_RestockDate
          ? new Date(initial.last_RestockDate).toISOString().split("T")[0]
          : "",
      });
    } else {
      setForm({
        product_ID: "",
        quantity: "",
        last_RestockDate: "",
      });
    }
  }, [initial]);

  const onChange = (e) => {
    const { name, value } = e.target;
    setForm((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const onSubmit = () => {
    if (!form.product_ID || !form.quantity) {
      setError(true);
      return;
    }
    handleSave(form);
  };

  return (
    <Modal show={show} onHide={handleClose}>
      <Modal.Header closeButton>
        <Modal.Title>{initial ? "Edit Stock" : "Add Stock"}</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <Form>
          <Form.Group className="mb-2">
            <Form.Label>Product</Form.Label>
            <Form.Select
              name="product_ID"
              value={form.product_ID}
              onChange={onChange}
              isInvalid={error && !form.product_ID}
            >
              <option value="">-- Select Product --</option>
              {products.map((p) => (
                <option key={p.product_ID} value={p.product_ID}>
                  {p.product_Name}
                </option>
              ))}
            </Form.Select>
            <Form.Control.Feedback type="invalid">
              Product is required.
            </Form.Control.Feedback>
          </Form.Group>

          <Form.Group className="mb-2">
            <Form.Label>Quantity</Form.Label>
            <Form.Control
              type="number"
              name="quantity"
              value={form.quantity}
              onChange={onChange}
              isInvalid={error && !form.quantity}
            />
            <Form.Control.Feedback type="invalid">
              Quantity is required.
            </Form.Control.Feedback>
          </Form.Group>

          <Form.Group className="mb-2">
            <Form.Label>Last Restock Date</Form.Label>
            <Form.Control
              type="date"
              name="last_RestockDate"
              value={form.last_RestockDate}
              onChange={onChange}
            />
          </Form.Group>
        </Form>
      </Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={handleClose}>
          Cancel
        </Button>
        <Button variant="primary" onClick={onSubmit}>
          Save
        </Button>
      </Modal.Footer>
    </Modal>
  );
};

export default AddEditStockModal;
