import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Table, Button } from 'react-bootstrap';

const PurchaseOrders = () => {
    const [purchaseOrders, setPurchaseOrders] = useState([]);

    useEffect(() => {
        const fetchPurchaseOrders = async () => {
            try {
                const response = await axios.get('/api/purchaseOrders');
                setPurchaseOrders(response.data);
            } catch (error) {
                console.error('Error fetching purchase orders:', error);
            }
        };

        fetchPurchaseOrders();
    }, []);

    const handleDelete = async (id) => {
        try {
            await axios.delete(`/api/purchaseOrders/${id}`);
            setPurchaseOrders(purchaseOrders.filter(order => order._id !== id));
        } catch (error) {
            console.error('Error deleting purchase order:', error);
        }
    };

    return (
        <div>
            <h2>Purchase Orders</h2>
            <Table striped bordered hover>
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Supplier</th>
                        <th>Product</th>
                        <th>Quantity</th>
                        <th>Date</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {purchaseOrders.map(order => (
                        <tr key={order._id}>
                            <td>{order._id}</td>
                            <td>{order.supplier}</td>
                            <td>{order.product}</td>
                            <td>{order.quantity}</td>
                            <td>{new Date(order.date).toLocaleDateString()}</td>
                            <td>
                                <Button variant="danger" onClick={() => handleDelete(order._id)}>Delete</Button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </Table>
        </div>
    );
};

export default PurchaseOrders;
