import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { PieChart, Pie, Cell, Legend, BarChart, Bar, XAxis, YAxis, Tooltip, ResponsiveContainer } from 'recharts';

const pieColors = ['#f7b267', '#f4845f', '#45b8ac', '#f76c6c', '#f7d08a'];

const Dashboard = () => {
  const [summary, setSummary] = useState({});
  const [inventoryData, setInventoryData] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      const summaryRes = await axios.get('/api/dashboard/summary');
      const inventoryRes = await axios.get('/api/dashboard/inventory-table');
      setSummary(summaryRes.data);
      setInventoryData(inventoryRes.data);
    };
    fetchData();
  }, []);

  const pieData = inventoryData.map(item => ({
    name: item.category,
    value: item.stock
  }));

  const barData = inventoryData.map(item => ({
    name: item.product,
    value: item.stock
  }));

  return (
    <div className="p-4" style={{ background: '#fff', minHeight: '80vh' }}>
      <h2 className="fw-bold mb-2" style={{ fontFamily: 'Poppins, Arial, sans-serif' }}>Welcome back, Admin</h2>
      <hr />
      <div className="mb-4">
        <span className="fs-4 fw-semibold">Dashboard</span>
      </div>
      <div className="row mb-4">
        <div className="col">
          <div className="border rounded-3 p-3 text-center">
            <div className="fw-semibold">Total Products</div>
            <div className="fs-3">{summary.totalProducts ?? '...'}</div>
          </div>
        </div>
        <div className="col">
          <div className="border rounded-3 p-3 text-center">
            <div className="fw-semibold">Total Suppliers</div>
            <div className="fs-3">{summary.totalSuppliers ?? '...'}</div>
          </div>
        </div>
        <div className="col">
          <div className="border rounded-3 p-3 text-center">
            <div className="fw-semibold">Pending Orders</div>
            <div className="fs-3">{summary.pendingOrders ?? '...'}</div>
          </div>
        </div>
        <div className="col">
          <div className="border rounded-3 p-3 text-center">
            <div className="fw-semibold">Stock Alert</div>
            <div className="fs-3">{summary.stockAlerts ?? '...'}</div>
          </div>
        </div>
      </div>

      <div className="row g-4">
        <div className="col-md-8">
          <div className="border rounded-3 p-3 mb-4">
            <table className="table table-bordered mb-0">
              <thead>
                <tr>
                  <th>Category</th>
                  <th>Product</th>
                  <th>ID</th>
                  <th>In Stock</th>
                </tr>
              </thead>
              <tbody>
                {inventoryData.map((item, idx) => (
                  <tr key={idx}>
                    <td>{item.category}</td>
                    <td>{item.product}</td>
                    <td>{item.id}</td>
                    <td>{item.stock}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>

          <div className="border rounded-3 p-3">
            <ResponsiveContainer width="100%" height={250}>
              <PieChart>
                <Pie
                  data={pieData}
                  dataKey="value"
                  nameKey="name"
                  cx="50%"
                  cy="50%"
                  outerRadius={80}
                  label
                >
                  {pieData.map((entry, idx) => (
                    <Cell key={`cell-${idx}`} fill={pieColors[idx % pieColors.length]} />
                  ))}
                </Pie>
                <Legend />
              </PieChart>
            </ResponsiveContainer>
          </div>
        </div>

        <div className="col-md-4">
          <div className="border rounded-3 p-3">
            <div className="fw-semibold mb-2">Stocks</div>
            <ResponsiveContainer width="100%" height={200}>
              <BarChart data={barData} layout="vertical">
                <XAxis type="number" hide />
                <YAxis type="category" dataKey="name" />
                <Tooltip />
                <Bar dataKey="value" fill="#45b8ac" />
              </BarChart>
            </ResponsiveContainer>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
