import React, { useEffect, useState } from 'react';
import { BarChart, Bar, XAxis, YAxis, Tooltip, ResponsiveContainer, PieChart, Pie, Cell } from 'recharts';
import { Package, Users, ShoppingCart, AlertTriangle } from 'lucide-react';
import 'bootstrap/dist/css/bootstrap.min.css';

const Dashboard = () => {
  const [summaryData, setSummaryData] = useState({});
  const [products, setProducts] = useState([]);
  const [categoryData, setCategoryData] = useState([]);
  const [stockLevels, setStockLevels] = useState([]);
  const [loading, setLoading] = useState(true);
  const API_BASE = 'http://localhost:5000/api';

  const COLORS = ['#10B981', '#F59E0B', '#EF4444', '#3B82F6', '#8B5CF6'];

  useEffect(() => {
    fetchDashboardData();
  }, []);

  const fetchDashboardData = async () => {
    try {
      setLoading(true);
      const summary = await (await fetch(`${API_BASE}/dashboard/summary`)).json();
      const products = await (await fetch(`${API_BASE}/dashboard/products`)).json();
      const categories = await (await fetch(`${API_BASE}/dashboard/category-distribution`)).json();
      const stock = await (await fetch(`${API_BASE}/dashboard/stock-levels`)).json();

      setSummaryData(summary);
      setProducts(products);
      setCategoryData(categories);
      setStockLevels(stock);
    } catch (error) {
      console.error('Failed to fetch dashboard:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container-fluid py-4 px-5 bg-light min-vh-100">
      <div className="mb-4">
        <h2 className="fw-bold">Dashboard</h2>
        <p className="text-muted">Welcome back, Admin</p>
      </div>

      {/* Summary Cards */}
      <div className="row g-4 mb-4">
        <SummaryCard icon={<Package />} label="Total Products" value={summaryData.totalProducts} color="success" />
        <SummaryCard icon={<Users />} label="Total Suppliers" value={summaryData.totalSuppliers} color="success" />
        <SummaryCard icon={<ShoppingCart />} label="Pending Orders" value={summaryData.pendingOrders} color="success" />
        <SummaryCard icon={<AlertTriangle />} label="Stock Alerts" value={summaryData.stockAlerts} color="danger" />
      </div>

      {/* Chart & Product Table */}
      <div className="row g-4 mb-4">
        <div className="col-lg-8">
          <div className="card shadow-sm">
            <div className="card-header bg-white border-0 d-flex justify-content-between align-items-center">
              <h6 className="mb-0">Recent Products</h6>
              <button className="btn btn-sm btn-outline-success" onClick={fetchDashboardData}>Refresh</button>
            </div>
            <div className="card-body p-0">
              <div className="table-responsive">
                <table className="table mb-0">
                  <thead className="table-light">
                    <tr>
                      <th>Category</th>
                      <th>Product</th>
                      <th>ID</th>
                      <th>Stock</th>
                    </tr>
                  </thead>
                  <tbody>
                    {products.map(p => (
                      <tr key={p.product_ID}>
                        <td>{p.category_Name || 'Uncategorized'}</td>
                        <td>{p.product_Name}</td>
                        <td>{p.product_ID}</td>
                        <td>
                          <span className={`badge bg-${p.stock_Quantity < 10 ? 'danger' : 'success'}`}>
                            {p.stock_Quantity}
                          </span>
                        </td>
                      </tr>
                    ))}
                    {products.length === 0 && (
                      <tr><td colSpan="4" className="text-center text-muted py-3">No data</td></tr>
                    )}
                  </tbody>
                </table>
              </div>
            </div>
          </div>
        </div>

        {/* Bar Chart */}
        <div className="col-lg-4">
          <div className="card shadow-sm">
            <div className="card-header bg-white border-0">
              <h6 className="mb-0">Stock Levels</h6>
            </div>
            <div className="card-body">
              <ResponsiveContainer width="100%" height={220}>
                <BarChart data={stockLevels}>
                  <XAxis dataKey="category_Name" tick={{ fontSize: 10 }} angle={-30} textAnchor="end" />
                  <YAxis />
                  <Tooltip />
                  <Bar dataKey="total_stock" fill="#10B981" />
                </BarChart>
              </ResponsiveContainer>
            </div>
          </div>
        </div>
      </div>

      {/* Pie Chart */}
      <div className="row mb-5">
        <div className="col-lg-6 mx-auto">
          <div className="card shadow-sm">
            <div className="card-header bg-white border-0">
              <h6 className="mb-0">Category Distribution</h6>
            </div>
            <div className="card-body">
              <ResponsiveContainer width="100%" height={250}>
                <PieChart>
                  <Pie
                    data={categoryData}
                    cx="50%"
                    cy="50%"
                    outerRadius={80}
                    dataKey="product_count"
                    label={({ category_Name, percentage }) => `${category_Name}: ${percentage}%`}
                  >
                    {categoryData.map((entry, index) => (
                      <Cell key={index} fill={COLORS[index % COLORS.length]} />
                    ))}
                  </Pie>
                  <Tooltip />
                </PieChart>
              </ResponsiveContainer>
              <div className="d-flex flex-wrap justify-content-center mt-3">
                {categoryData.map((entry, index) => (
                  <div key={index} className="d-flex align-items-center mx-2">
                    <div className="me-2 rounded-circle" style={{
                      backgroundColor: COLORS[index % COLORS.length],
                      width: '10px', height: '10px'
                    }}></div>
                    <span className="small">{entry.category_Name}</span>
                  </div>
                ))}
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

// SummaryCard Component
const SummaryCard = ({ icon, label, value = 0, color }) => (
  <div className="col-sm-6 col-lg-3">
    <div className={`card shadow-sm border-start border-${color} border-4`}>
      <div className="card-body d-flex align-items-center justify-content-between">
        <div>
          <small className="text-muted">{label}</small>
          <h5 className="fw-bold">{value}</h5>
        </div>
        <div className={`text-${color}`}>
          {icon}
        </div>
      </div>
    </div>
  </div>
);

export default Dashboard;
