import React, { useState, useEffect } from 'react';
import { Modal, Button, Form } from 'react-bootstrap';

const AddEditOrderModal = ({ show, handleClose, handleSave, initial, suppliers = [], products = [] }) => {
  const [form, setForm] = useState({
    supplier_ID: '',
    product_ID: '',
    quantity_Ordered: '',
    order_Date: '',
    unit_Price: '',
    status: 'Pending'
  });

  useEffect(() => {
    setForm(initial ? {
      supplier_ID: initial.supplier_ID || '',
      product_ID: initial.product_ID || '',
      quantity_Ordered: initial.quantity_Ordered || '',
      order_Date: initial.order_Date || '',
      unit_Price: initial.unit_Price || '',
      status: initial.status || 'Pending'
    } : {
      supplier_ID: '',
      product_ID: '',
      quantity_Ordered: '',
      order_Date: '',
      unit_Price: '',
      status: 'Pending'
    });
  }, [initial]);

  const onChange = e => setForm({ ...form, [e.target.name]: e.target.value });

  const onSave = () => {
    handleSave(form);
    setForm({
      supplier_ID: '',
      product_ID: '',
      quantity_Ordered: '',
      order_Date: '',
      unit_Price: '',
      status: 'Pending'
    });
  };

  return (
    <Modal show={show} onHide={handleClose} centered>
      <Modal.Header closeButton>
        <Modal.Title>{initial ? 'Edit' : 'Add'} Purchase Order</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <Form>
          <Form.Group className="mb-2">
            <Form.Label>Supplier</Form.Label>
            <Form.Select name="supplier_ID" value={form.supplier_ID} onChange={onChange}>
              <option value="">Select Supplier</option>
              {suppliers.map(sup => (
                <option key={sup.supplier_ID} value={sup.supplier_ID}>{sup.supplier_Company}</option>
              ))}
            </Form.Select>
          </Form.Group>
          <Form.Group className="mb-2">
            <Form.Label>Product</Form.Label>
            <Form.Select name="product_ID" value={form.product_ID} onChange={onChange}>
              <option value="">Select Product</option>
              {products.map(prod => (
                <option key={prod.product_ID} value={prod.product_ID}>{prod.product_Name}</option>
              ))}
            </Form.Select>
          </Form.Group>
          <Form.Group className="mb-2">
            <Form.Label>Quantity</Form.Label>
            <Form.Control name="quantity_Ordered" type="number" value={form.quantity_Ordered} onChange={onChange} />
          </Form.Group>
          <Form.Group className="mb-2">
            <Form.Label>Order Date</Form.Label>
            <Form.Control name="order_Date" type="date" value={form.order_Date} onChange={onChange} />
          </Form.Group>
          <Form.Group className="mb-2">
            <Form.Label>Unit Price</Form.Label>
            <Form.Control name="unit_Price" type="number" value={form.unit_Price} onChange={onChange} />
          </Form.Group>
          <Form.Group className="mb-2">
            <Form.Label>Status</Form.Label>
            <Form.Select name="status" value={form.status} onChange={onChange}>
              <option value="Pending">Pending</option>
              <option value="Completed">Completed</option>
              <option value="Cancelled">Cancelled</option>
            </Form.Select>
          </Form.Group>
        </Form>
      </Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={handleClose}>Cancel</Button>
        <Button variant="primary" onClick={onSave}>Save</Button>
      </Modal.Footer>
    </Modal>
  );
};

export default AddEditOrderModal;
