import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import './styles/bootstrap-custom.css';

import CategoryList from './components/CategoryList';
import SupplierList from './components/SupplierList';
import ProductList from './components/ProductList';
import StockManagement from './components/StockManagement';
import PurchaseOrders from './components/PurchaseOrders';
import UserManagement from './components/UserManagement';

function App() {
  // Example empty data arrays (replace with API data)
  const categories = [];
  const suppliers = [];
  const products = [];
  const stock = [];
  const orders = [];
  const users = [];

  return (
    <div style={{ minHeight: '100vh', background: '#f6f7fb' }}>
      {/* Top Navbar */}
      <nav className="navbar navbar-expand-lg navbar-light bg-white shadow-sm px-4" style={{ height: 70 }}>
        <span className="navbar-brand fw-bold" style={{ color: '#2d3748', fontSize: 24 }}>Supermarket Inventory Management</span>
        <div className="ms-auto">
          <img src="https://ui-avatars.com/api/?name=User" alt="User" className="rounded-circle" width={40} />
        </div>
      </nav>
      <div className="d-flex">
        {/* Sidebar */}
        <div className="bg-primary text-white p-4 shadow-sm" style={{ width: 240, minHeight: 'calc(100vh - 70px)' }}>
          <h2 className="mb-4 fw-bold fs-4">Supermarket IMS</h2>
          <nav className="nav flex-column">
            <a className="nav-link text-white" href="#">Dashboard</a>
            <a className="nav-link text-white" href="#">Categories</a>
            <a className="nav-link text-white" href="#">Suppliers</a>
            <a className="nav-link text-white" href="#">Products</a>
            <a className="nav-link text-white" href="#">Stock</a>
            <a className="nav-link text-white" href="#">Orders</a>
            <a className="nav-link text-white" href="#">Users</a>
          </nav>
        </div>
        {/* Main Content */}
        <div className="flex-grow-1 p-4">
          <div className="mb-4">
            <h1 className="fw-bold" style={{ color: '#2d3748' }}>Dashboard</h1>
          </div>
          <div className="row g-4">
            <div className="col-12 col-lg-6">
              <CategoryList categories={categories} />
              <SupplierList suppliers={suppliers} />
              <ProductList products={products} />
            </div>
            <div className="col-12 col-lg-6">
              <StockManagement stock={stock} />
              <PurchaseOrders orders={orders} />
              <UserManagement users={users} />
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
