import React, { useState, useEffect } from 'react';
import { Form, Button } from 'react-bootstrap';

function AddEditOrderModal({ initial, onSave, onClose, suppliers, products }) {
  const today = new Date().toISOString().split('T')[0];

  const [form, setForm] = useState({
    supplier_ID: '',
    product_ID: '',
    quantity_Ordered: '',
    order_Date: today,
    unit_Price: '',
    status: 'Pending',
  });

  useEffect(() => {
    if (initial) {
      setForm({
        supplier_ID: initial.supplier_ID || '',
        product_ID: initial.product_ID || '',
        quantity_Ordered: initial.quantity_Ordered || '',
        order_Date: initial.order_Date || today,
        unit_Price: initial.unit_Price || '',
        status: initial.status || 'Pending',
      });
    }
  }, [initial]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSave(form);
  };

  return (
    <Form onSubmit={handleSubmit}>
      <Form.Group>
        <Form.Label>Supplier</Form.Label>
        <Form.Control
          as="select"
          name="supplier_ID"
          value={form.supplier_ID}
          onChange={handleChange}
          required
        >
          <option value="">Select Supplier</option>
          {suppliers.map(sup => (
            <option key={sup.supplier_ID} value={sup.supplier_ID}>
              {sup.supplier_Company}
            </option>
          ))}
        </Form.Control>
      </Form.Group>

      <Form.Group>
        <Form.Label>Product</Form.Label>
        <Form.Control
          as="select"
          name="product_ID"
          value={form.product_ID}
          onChange={handleChange}
          required
        >
          <option value="">Select Product</option>
          {products.map(prod => (
            <option key={prod.product_ID} value={prod.product_ID}>
              {prod.product_Name}
            </option>
          ))}
        </Form.Control>
      </Form.Group>

      <Form.Group>
        <Form.Label>Quantity Ordered</Form.Label>
        <Form.Control
          type="number"
          name="quantity_Ordered"
          value={form.quantity_Ordered}
          onChange={handleChange}
          required
        />
      </Form.Group>

      <Form.Group>
        <Form.Label>Order Date</Form.Label>
        <Form.Control
          type="date"
          name="order_Date"
          value={form.order_Date}
          onChange={handleChange}
          required
        />
      </Form.Group>

      <Form.Group>
        <Form.Label>Unit Price</Form.Label>
        <Form.Control
          type="number"
          step="0.01"
          name="unit_Price"
          value={form.unit_Price}
          onChange={handleChange}
          required
        />
      </Form.Group>

      <Form.Group>
        <Form.Label>Status</Form.Label>
        <Form.Control
          as="select"
          name="status"
          value={form.status}
          onChange={handleChange}
          required
        >
          <option value="Pending">Pending</option>
          <option value="Completed">Completed</option>
          <option value="Cancelled">Cancelled</option>
        </Form.Control>
      </Form.Group>

      <div className="d-flex justify-content-end mt-3">
        <Button variant="secondary" onClick={onClose} className="me-2">Cancel</Button>
        <Button type="submit" variant="primary">Save</Button>
      </div>
    </Form>
  );
}

export default AddEditOrderModal;
