import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Table, Button, Form, InputGroup, Spinner, Alert, Card } from 'react-bootstrap';


const PurchaseOrders = () => {
  const [purchaseOrders, setPurchaseOrders] = useState([]);
  const [suppliers, setSuppliers] = useState([]);
  const [formData, setFormData] = useState({
    supplier_ID: '',
    order_Date: '',
    quantity_Ordered: '',
    unit_Price: '',
    status: 'Pending'
  });
  const [editingId, setEditingId] = useState(null);

  useEffect(() => {
    fetchPurchaseOrders();
    fetchSuppliers();
  }, []);

  const fetchPurchaseOrders = async () => {
    try {
      const res = await axios.get('/api/purchaseorders');
      setPurchaseOrders(res.data);
    } catch (err) {
      console.error('Error fetching purchase orders:', err);
    }
  };

  const fetchSuppliers = async () => {
    try {
      const res = await axios.get('/api/suppliers');
      setSuppliers(res.data);
    } catch (err) {
      console.error('Error fetching suppliers:', err);
    }
  };

  const handleChange = e => {
    setFormData(prev => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const handleSubmit = async e => {
    e.preventDefault();
    try {
      if (editingId) {
        await axios.put(`/api/purchaseorders/${editingId}`, formData);
      } else {
        await axios.post('/api/purchaseorders', formData);
      }
      fetchPurchaseOrders();
      resetForm();
    } catch (err) {
      console.error('Error saving purchase order:', err);
    }
  };

  const handleEdit = (po) => {
    setEditingId(po.purchase_OrderID);
    setFormData({
      supplier_ID: po.supplier_ID,
      order_Date: po.order_Date.split('T')[0],
      quantity_Ordered: po.quantity_Ordered,
      unit_Price: po.unit_Price,
      status: po.status
    });
  };

  const handleDelete = async (id) => {
    if (!window.confirm('Are you sure you want to delete this purchase order?')) return;
    try {
      await axios.delete(`/api/purchaseorders/${id}`);
      fetchPurchaseOrders();
    } catch (err) {
      console.error('Error deleting purchase order:', err);
    }
  };

  const resetForm = () => {
    setFormData({
      supplier_ID: '',
      order_Date: '',
      quantity_Ordered: '',
      unit_Price: '',
      status: 'Pending'
    });
    setEditingId(null);
  };

  const getSupplierCompany = (supplier_ID) => {
    const supplier = suppliers.find(s => s.supplier_ID === supplier_ID);
    return supplier ? supplier.supplier_Company : 'Unknown';
  };

  return (
    <div className="container mt-4">
      <h2 className="mb-4">{editingId ? 'Edit Purchase Order' : 'Add Purchase Order'}</h2>
      <form onSubmit={handleSubmit} className="row g-3 mb-4">
        <div className="col-md-4">
          <label className="form-label">Supplier</label>
          <select className="form-select" name="supplier_ID" value={formData.supplier_ID} onChange={handleChange} required>
            <option value="">Select supplier</option>
            {suppliers.map(s => (
              <option key={s.supplier_ID} value={s.supplier_ID}>
                {s.supplier_Company}
              </option>
            ))}
          </select>
        </div>
        <div className="col-md-3">
          <label className="form-label">Order Date</label>
          <input type="date" className="form-control" name="order_Date" value={formData.order_Date} onChange={handleChange} required />
        </div>
        <div className="col-md-2">
          <label className="form-label">Quantity</label>
          <input type="number" className="form-control" name="quantity_Ordered" value={formData.quantity_Ordered} onChange={handleChange} required />
        </div>
        <div className="col-md-2">
          <label className="form-label">Unit Price</label>
          <input type="number" step="0.01" className="form-control" name="unit_Price" value={formData.unit_Price} onChange={handleChange} required />
        </div>
        <div className="col-md-3">
          <label className="form-label">Status</label>
          <select className="form-select" name="status" value={formData.status} onChange={handleChange} required>
            <option>Pending</option>
            <option>Completed</option>
            <option>Cancelled</option>
          </select>
        </div>
        <div className="col-12">
          <button type="submit" className="btn btn-primary me-2">{editingId ? 'Update' : 'Add'}</button>
          {editingId && <button type="button" className="btn btn-secondary" onClick={resetForm}>Cancel</button>}
        </div>
      </form>

      <h3>Purchase Orders</h3>
      <table className="table table-striped">
        <thead>
          <tr>
            <th>ID</th>
            <th>Supplier</th>
            <th>Date</th>
            <th>Quantity</th>
            <th>Unit Price</th>
            <th>Status</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {purchaseOrders.map(po => (
            <tr key={po.purchase_OrderID}>
              <td>{po.purchase_OrderID}</td>
              <td>{getSupplierCompany(po.supplier_ID)}</td>
              <td>{new Date(po.order_Date).toLocaleDateString()}</td>
              <td>{po.quantity_Ordered}</td>
              <td>{po.unit_Price}</td>
              <td>{po.status}</td>
              <td>
                <button className="btn btn-sm btn-warning me-2" onClick={() => handleEdit(po)}>Edit</button>
                <button className="btn btn-sm btn-danger" onClick={() => handleDelete(po.purchase_OrderID)}>Delete</button>
              </td>
            </tr>
          ))}
          {purchaseOrders.length === 0 && (
            <tr>
              <td colSpan="7" className="text-center">No purchase orders found</td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
};

export default PurchaseOrders;
