import React, { useState, useEffect } from 'react';
import { Modal, Button, Form } from 'react-bootstrap';

const AddEditSupplierModal = ({ show, handleClose, handleSave, initial }) => {
  const [form, setForm] = useState({
    supplier_Company: '',
    contact_Person: '',
    supplier_ContactNumber: '',
    supplier_Email: '',
    supplier_Address: ''
  });

  useEffect(() => {
    setForm(initial ? {
      supplier_Company: initial.supplier_Company || '',
      contact_Person: initial.contact_Person || '',
      supplier_ContactNumber: initial.supplier_ContactNumber || '',
      supplier_Email: initial.supplier_Email || '',
      supplier_Address: initial.supplier_Address || ''
    } : {
      supplier_Company: '',
      contact_Person: '',
      supplier_ContactNumber: '',
      supplier_Email: '',
      supplier_Address: ''
    });
  }, [initial]);

  const onChange = e => setForm({ ...form, [e.target.name]: e.target.value });

  const onSave = () => {
    handleSave(form);
    setForm({
      supplier_Company: '',
      contact_Person: '',
      supplier_ContactNumber: '',
      supplier_Email: '',
      supplier_Address: ''
    });
  };

  return (
    <Modal show={show} onHide={handleClose} centered>
      <Modal.Header closeButton>
        <Modal.Title>{initial ? 'Edit' : 'Add'} Supplier</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <Form>
          <Form.Group className="mb-2">
            <Form.Label>Company Name</Form.Label>
            <Form.Control name="supplier_Company" value={form.supplier_Company} onChange={onChange} />
          </Form.Group>
          <Form.Group className="mb-2">
            <Form.Label>Contact Person</Form.Label>
            <Form.Control name="contact_Person" value={form.contact_Person} onChange={onChange} />
          </Form.Group>
          <Form.Group className="mb-2">
            <Form.Label>Contact Number</Form.Label>
            <Form.Control name="supplier_ContactNumber" value={form.supplier_ContactNumber} onChange={onChange} />
          </Form.Group>
          <Form.Group className="mb-2">
            <Form.Label>Email</Form.Label>
            <Form.Control name="supplier_Email" value={form.supplier_Email} onChange={onChange} />
          </Form.Group>
          <Form.Group className="mb-2">
            <Form.Label>Address</Form.Label>
            <Form.Control name="supplier_Address" value={form.supplier_Address} onChange={onChange} />
          </Form.Group>
        </Form>
      </Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={handleClose}>Cancel</Button>
        <Button variant="primary" onClick={onSave}>Save</Button>
      </Modal.Footer>
    </Modal>
  );
};

export default AddEditSupplierModal;
