import React, { useEffect, useState, useRef } from 'react';
import axios from 'axios';
import { Table, Button, Form, InputGroup, Alert } from 'react-bootstrap';
import { FaPlus, FaEdit, FaSearch } from 'react-icons/fa';
import AddEditStockModal from './AddEditStockModal';

const StockManagement = () => {
  const [stocks, setStocks] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [editStock, setEditStock] = useState(null);
  const [editIndex, setEditIndex] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [search, setSearch] = useState('');
  const editPopupRef = useRef(null);

  useEffect(() => {
    fetchStocks();
    const handleClickOutside = (event) => {
      if (editPopupRef.current && !editPopupRef.current.contains(event.target)) {
        setEditIndex(null);
      }
    };
    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, []);

  const fetchStocks = async () => {
    setLoading(true);
    setError('');
    try {
      const res = await axios.get('http://localhost:5000/api/stock');
      setStocks(res.data);
    } catch (err) {
      setError('Failed to fetch stock data.');
    }
    setLoading(false);
  };

  const handleSave = async (stock) => {
    setLoading(true);
    setError('');
    try {
      if (editStock) {
        await axios.put(`http://localhost:5000/api/stock/${editStock.stock_ID}`, stock);
      } else {
        await axios.post('http://localhost:5000/api/stock', stock);
      }
      fetchStocks();
      setShowModal(false);
      setEditStock(null);
      setEditIndex(null);
    } catch (err) {
      setError('Failed to save stock.');
    }
    setLoading(false);
  };

  const handleEdit = (stock, idx) => {
    setEditStock(stock);
    setEditIndex(idx);
    setShowModal(false);
  };

  const handleDelete = async (id) => {
    if (window.confirm('Are you sure you want to delete this stock record?')) {
      setLoading(true);
      setError('');
      try {
        await axios.delete(`http://localhost:5000/api/stock/${id}`);
        fetchStocks();
      } catch (err) {
        setError('Failed to delete stock.');
      }
      setLoading(false);
    }
  };

  const handleShowAdd = () => {
    setEditStock(null);
    setShowModal(true);
    setEditIndex(null);
  };

  const filteredStocks = stocks.filter(
    (s) =>
      s.product_Name?.toLowerCase().includes(search.toLowerCase()) ||
      s.stock_Status?.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div className="container my-4">
      {/* Search Bar */}
      <div className="d-flex flex-wrap gap-2 mb-3">
        <InputGroup>
          <Form.Control
            placeholder="Search by Product or Status"
            value={search}
            onChange={(e) => setSearch(e.target.value)}
            style={{ maxWidth: 300 }}
          />
          <FaSearch style={{ position: 'absolute', left: 270, top: 10 }} />
        </InputGroup>
      </div>

      {/* Stock Table */}
      <div className="card shadow-sm">
        <div className="d-flex align-items-center justify-content-between px-4 py-2" style={{ borderBottom: '1px solid #eee' }}>
          <span className="fw-bold" style={{ fontSize: 21 }}>Stock</span>
          <Button variant="success" style={{ maxHeight: 30, position: 'absolute', right: 15 }} onClick={handleShowAdd}>
            <FaPlus style={{ marginBottom: 9 }} />
          </Button>
        </div>
        {error && <Alert variant="danger" className="m-3">{error}</Alert>}
        {loading ? (
          <div className="text-center my-4">Loading...</div>
        ) : (
          <Table hover className="mb-0 table-striped" style={{ background: 'transparent' }}>
            <thead>
              <tr>
                <th>Product</th>
                <th>Quantity</th>
                <th>Status</th>
                <th>Date Updated</th>
                <th style={{ width: 80 }}>Actions</th>
              </tr>
            </thead>
            <tbody>
              {filteredStocks.length === 0 ? (
                <tr>
                  <td colSpan="5" className="text-center text-muted">No stock found.</td>
                </tr>
              ) : (
                filteredStocks.map((stock, idx) => (
                  <tr key={stock.stock_ID}>
                    <td>{stock.product_Name}</td>
                    <td>{stock.stock_Quantity}</td>
                    <td>{stock.stock_Status}</td>
                    <td>{new Date(stock.stock_DateUpdated).toLocaleDateString()}</td>
                    <td style={{ position: 'relative' }}>
                      <Button
                        variant="outline-success"
                        size="sm"
                        style={{ borderRadius: '50%' }}
                        onClick={() => handleEdit(stock, idx)}
                      >
                        <FaEdit />
                      </Button>

                      {editIndex === idx && (
                        <div
                          ref={editPopupRef}
                          style={{
                            position: 'absolute',
                            top: 0,
                            right: 40,
                            background: '#fff',
                            border: '1px solid #ccc',
                            borderRadius: 8,
                            boxShadow: '0 2px 8px #0002',
                            zIndex: 10,
                            padding: 12,
                            display: 'flex',
                            flexDirection: 'column',
                            gap: 8
                          }}
                        >
                          <Button
                            variant="success"
                            size="sm"
                            onClick={() => { setShowModal(true); setEditIndex(null); }}
                            style={{ minWidth: 70 }}
                          >
                            Edit
                          </Button>
                          <Button
                            variant="danger"
                            size="sm"
                            onClick={() => handleDelete(stock.stock_ID)}
                            style={{ minWidth: 70 }}
                          >
                            Delete
                          </Button>
                        </div>
                      )}
                    </td>
                  </tr>
                ))
              )}
            </tbody>
          </Table>
        )}
      </div>

      {/* Modal */}
      {showModal && (
        <AddEditStockModal
          show={showModal}
          handleClose={() => { setShowModal(false); setEditStock(null); }}
          handleSave={handleSave}
          initial={editStock}
        />
      )}
    </div>
  );
};

export default StockManagement;
