import React, { useEffect, useState, useRef } from 'react';
import axios from 'axios';
import { Table, Button, Form, InputGroup, Alert, Spinner } from 'react-bootstrap';
import { FaPlus, FaEdit } from 'react-icons/fa';
import AddEditStockModal from './AddEditStockModal';

const StockManagement = () => {
  const [stock, setStock] = useState([]);
  const [products, setProducts] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [editStock, setEditStock] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [search, setSearch] = useState('');
  const popupRef = useRef(null);

  useEffect(() => {
    fetchStock();
    fetchProducts();

    // Close popup on outside click
    const handleClickOutside = (event) => {
      if (popupRef.current && !popupRef.current.contains(event.target)) {
        setEditStock(null);
      }
    };
    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, []);

  const fetchStock = async () => {
    setLoading(true);
    setError('');
    try {
      const res = await axios.get('http://localhost:5000/api/stock');
      setStock(res.data);
    } catch (err) {
      setError('Failed to fetch stock.');
    }
    setLoading(false);
  };

  const fetchProducts = async () => {
    try {
      const res = await axios.get('http://localhost:5000/api/products');
      setProducts(res.data);
    } catch (err) {
      setError('Failed to fetch products.');
    }
  };

  const handleSave = async (stockItem) => {
    setLoading(true);
    setError('');
    try {
      if (editStock) {
        await axios.put(`http://localhost:5000/api/stock/${editStock.stock_ID}`, stockItem);
      } else {
        await axios.post('http://localhost:5000/api/stock', stockItem);
      }
      fetchStock();
      setShowModal(false);
      setEditStock(null);
    } catch (err) {
      setError('Failed to save stock.');
    }
    setLoading(false);
  };

  const handleDelete = async (id) => {
    if (window.confirm('Are you sure you want to delete this stock item?')) {
      setLoading(true);
      setError('');
      try {
        await axios.delete(`http://localhost:5000/api/stock/${id}`);
        fetchStock();
      } catch (err) {
        setError('Failed to delete stock.');
      }
      setLoading(false);
    }
  };

  const handleShowAdd = () => {
    setEditStock(null);
    setShowModal(true);
  };

  const filteredStock = stock.filter(item =>
    item.product_Name.toLowerCase().includes(search.toLowerCase()) ||
    item.category_Name.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div className="container my-4">
      {/* Search Bar */}
      <div className="d-flex flex-wrap gap-2 mb-3">
        <InputGroup>
          <Form.Control
            placeholder="Search Stock"
            value={search}
            onChange={(e) => setSearch(e.target.value)}
            style={{ minWidth: 220 }}
          />
        </InputGroup>
      </div>

      {/* Stock Table */}
      <div className="card shadow-sm">
        <div className="d-flex align-items-center justify-content-between px-4 py-2" style={{ borderBottom: '1px solid #eee' }}>
          <span className="fw-bold" style={{ fontSize: 22 }}>Stock</span>
          <Button variant="success" onClick={handleShowAdd}>
            <FaPlus />
          </Button>
        </div>
        {error && <Alert variant="danger" className="m-3">{error}</Alert>}
        {loading ? (
          <div className="text-center my-4">
            <Spinner animation="border" />
          </div>
        ) : (
          <Table hover className="mb-0 table-striped" style={{ background: 'transparent' }}>
            <thead>
              <tr>
                <th>Product Name</th>
                <th>Category</th>
                <th>Supplier</th>
                <th>Stock Quantity</th>
                <th>Last Restock Date</th>
                <th></th>
              </tr>
            </thead>
            <tbody>
              {filteredStock.length === 0 ? (
                <tr>
                  <td colSpan="6" className="text-center text-muted">No stock data found.</td>
                </tr>
              ) : (
                filteredStock.map((item) => (
                  <tr key={item.stock_ID}>
                    <td>{item.product_Name}</td>
                    <td>{item.category_Name}</td>
                    <td>{item.supplier_Company}</td>
                    <td>{item.stock_Quantity}</td>
                    <td>{new Date(item.last_RestockDate).toLocaleDateString()}</td>
                    <td>
                      <Button
                        variant="outline-success"
                        size="sm"
                        onClick={() => { setEditStock(item); setShowModal(true); }}
                      >
                        <FaEdit />
                      </Button>
                    </td>
                  </tr>
                ))
              )}
            </tbody>
          </Table>
        )}
      </div>

      {/* Add/Edit Stock Modal */}
      {showModal && (
        <AddEditStockModal
          show={showModal}
          handleClose={() => setShowModal(false)}
          handleSave={handleSave}
          initial={editStock}
          products={products}
        />
      )}
    </div>
  );
};

export default StockManagement;
