import React, { useEffect, useState } from 'react';
import {
  Table,
  Button,
  Container,
  Row,
  Col,
  Spinner,
  Alert,
} from 'react-bootstrap';
import AddEditOrderModal from './AddEditOrderModal';
import {
  fetchOrders,
  createOrder,
  updateOrder,
  deleteOrder,
} from '../routes/purchaseOrders';
import { fetchSuppliers } from '../routes/supplierApi';

const PurchaseOrders = () => {
  const [orders, setOrders] = useState([]);
  const [suppliers, setSuppliers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [modalShow, setModalShow] = useState(false);
  const [selectedOrder, setSelectedOrder] = useState(null);
  const [error, setError] = useState('');

  useEffect(() => {
    loadData();
  }, []);

  const loadData = async () => {
    setLoading(true);
    try {
      const [orderData, supplierData] = await Promise.all([
        fetchOrders(),
        fetchSuppliers(),
      ]);
      setOrders(orderData);
      setSuppliers(supplierData);
    } catch (err) {
      setError('Failed to load purchase orders.');
    } finally {
      setLoading(false);
    }
  };

  const handleSave = async (orderData) => {
    try {
      if (selectedOrder) {
        await updateOrder(selectedOrder.purchase_OrderID, orderData);
      } else {
        await createOrder(orderData);
      }
      await loadData();
    } catch (err) {
      console.error('Error saving order:', err);
      setError('Failed to save purchase order.');
    }
  };

  const handleDelete = async (id) => {
    if (!window.confirm('Are you sure you want to delete this order?')) return;

    try {
      await deleteOrder(id);
      await loadData();
    } catch (err) {
      console.error('Error deleting order:', err);
      setError('Failed to delete purchase order.');
    }
  };

  const getSupplierName = (supplier_ID) => {
    const supplier = suppliers.find((s) => s.supplier_ID === supplier_ID);
    return supplier ? supplier.supplier_Company : 'Unknown Supplier';
  };

  return (
    <Container className="mt-4">
      <Row className="mb-3">
        <Col>
          <h2>Purchase Orders</h2>
        </Col>
        <Col className="text-end">
          <Button onClick={() => { setSelectedOrder(null); setModalShow(true); }}>
            Add Order
          </Button>
        </Col>
      </Row>

      {error && <Alert variant="danger">{error}</Alert>}

      {loading ? (
        <div className="text-center py-5">
          <Spinner animation="border" />
        </div>
      ) : (
        <Table bordered hover responsive>
          <thead className="table-light">
            <tr>
              <th>#</th>
              <th>Supplier</th>
              <th>Quantity</th>
              <th>Order Date</th>
              <th>Unit Price</th>
              <th>Status</th>
              <th style={{ width: '130px' }}>Actions</th>
            </tr>
          </thead>
          <tbody>
            {orders.map((order, idx) => (
              <tr key={order.purchase_OrderID}>
                <td>{idx + 1}</td>
                <td>{getSupplierName(order.supplier_ID)}</td>
                <td>{order.quantity_Ordered}</td>
                <td>{order.order_Date}</td>
                <td>{order.unit_Price}</td>
                <td>{order.status}</td>
                <td>
                  <Button
                    size="sm"
                    variant="outline-primary"
                    className="me-2"
                    onClick={() => {
                      setSelectedOrder(order);
                      setModalShow(true);
                    }}
                  >
                    Edit
                  </Button>
                  <Button
                    size="sm"
                    variant="outline-danger"
                    onClick={() => handleDelete(order.purchase_OrderID)}
                  >
                    Delete
                  </Button>
                </td>
              </tr>
            ))}
          </tbody>
        </Table>
      )}

      <AddEditOrderModal
        show={modalShow}
        handleClose={() => setModalShow(false)}
        handleSave={handleSave}
        initial={selectedOrder}
        suppliers={suppliers}
      />
    </Container>
  );
};

export default PurchaseOrders;
