import React, { useState } from 'react';
import '../styles/login.css';
import { FaUser, FaLock, FaEye, FaEyeSlash } from 'react-icons/fa';
import { useAuth } from '../context/AuthContext';
import 'bootstrap/dist/css/bootstrap.min.css';

const Login = () => {
  const { login } = useAuth();
  const [showPassword, setShowPassword] = useState(false);
  const [form, setForm] = useState({ username: '', password: '', remember: false });
  const [error, setError] = useState('');

  const handleChange = e => {
    const { name, value, type, checked } = e.target;
    setForm(f => ({ ...f, [name]: type === 'checkbox' ? checked : value }));
  };

  const handleSubmit = async e => {
    e.preventDefault();
    const success = await login(form.username, form.password);
    if (!success) setError('Invalid credentials');
  };

  const loginRightStyle = {
    flex: 1,
    background: `url(${process.env.PUBLIC_URL}/login-bg.jpg) center center/cover no-repeat`,
    minHeight: '100vh',
    display: 'flex',
    alignItems: 'flex-end',
    justifyContent: 'flex-end',
    borderRadius: 0
  };

  return (
    <div className="login-container">
      <div className="login-left">
        <img src="/logo192.png" alt="MarketTrack Logo" className="login-logo" />
        <div className="login-title">MarketTrack</div>
        <div className="login-subtitle">
          Stay organized. Track products. Simplify inventory.
        </div>
        <form className="login-form-card" onSubmit={handleSubmit}>
          <div className="login-input-group">
            <FaUser />
            <input
              className="login-input"
              type="text"
              name="username"
              placeholder="Username"
              value={form.username}
              onChange={handleChange}
              required
            />
          </div>
          <div className="login-input-group">
            <FaLock />
            <input
              className="login-input"
              type={showPassword ? 'text' : 'password'}
              name="password"
              placeholder="Password"
              value={form.password}
              onChange={handleChange}
              required
            />
            <span
              style={{ cursor: 'pointer' }}
              onClick={() => setShowPassword(v => !v)}
              tabIndex={0}
              aria-label="Toggle password visibility"
            >
              {showPassword ? <FaEyeSlash /> : <FaEye />}
            </span>
          </div>
          <div className="login-form-row">
            <label>
              Remember Me?
              <input
                type="checkbox"
                name="remember"
                checked={form.remember}
                onChange={handleChange}
                style={{ marginLeft: 4 }}
              />
            </label>
            <a href="#">Forgot password?</a>
          </div>
          <div className="login-btn-row">
            <button className="login-btn" type="submit">Login</button>
            <button className="login-btn" type="button" onClick={() => setForm({ username: '', password: '', remember: false })}>Cancel</button>
          </div>
          {error && <div className="alert alert-danger mt-3">{error}</div>}
        </form>
      </div>
      <div className="login-right" style={loginRightStyle}></div>
    </div>
  );
};

export default Login;
