import React, { useEffect, useState } from "react";
import {
  Button,
  Card,
  Col,
  Container,
  Form,
  Row,
  Spinner,
  Table,
} from "react-bootstrap";
import {
  getAllStocks,
  createStock,
  updateStock,
  deleteStock,
} from "../services/stockService";
import { getAllProducts } from "../routes/product";
import { FaPlus, FaEdit, FaTrash } from "react-icons/fa";
import AddEditStockModal from "./AddEditStockModal";

const StockManagement = () => {
  const [stocks, setStocks] = useState([]);
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [search, setSearch] = useState("");
  const [showModal, setShowModal] = useState(false);
  const [editStock, setEditStock] = useState(null);

  const fetchData = async () => {
    try {
      setLoading(true);
      const [stockRes, productRes] = await Promise.all([
        getAllStocks(),
        getAllProducts(),
      ]);
      setStocks(stockRes.data);
      setProducts(productRes.data);
    } catch (error) {
      console.error("Error fetching stock data:", error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  const handleSave = async (stockData) => {
    try {
      if (editStock) {
        await updateStock(editStock.stock_ID, stockData);
      } else {
        await createStock(stockData);
      }
      setShowModal(false);
      setEditStock(null);
      fetchData();
    } catch (error) {
      console.error("Error saving stock:", error);
    }
  };

  const handleDelete = async (stockId) => {
    if (window.confirm("Are you sure you want to delete this stock item?")) {
      try {
        await deleteStock(stockId);
        fetchData();
      } catch (error) {
        console.error("Error deleting stock:", error);
      }
    }
  };

  const handleEdit = (item) => {
    setEditStock(item);
    setShowModal(true);
  };

  const filteredStocks = stocks.filter((stock) =>
    stock.product_Name?.toLowerCase().includes(search.toLowerCase()) ||
    stock.category_Name?.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <Container fluid className="p-4">
      <Row className="mb-3">
        <Col md={6}>
          <h4>Stock Management</h4>
        </Col>
        <Col md={6} className="text-end">
          <Button onClick={() => setShowModal(true)}>
            <FaPlus className="me-2" /> Add Stock
          </Button>
        </Col>
      </Row>
      <Card>
        <Card.Body>
          <Row className="mb-3">
            <Col md={4}>
              <Form.Control
                type="text"
                placeholder="Search by Product or Category"
                value={search}
                onChange={(e) => setSearch(e.target.value)}
              />
            </Col>
          </Row>
          {loading ? (
            <div className="text-center my-4">
              <Spinner animation="border" variant="primary" />
            </div>
          ) : (
            <Table striped bordered hover responsive>
              <thead>
                <tr>
                  <th>#</th>
                  <th>Product</th>
                  <th>Category</th>
                  <th>Quantity</th>
                  <th>Last Restock</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {filteredStocks.map((item, index) => (
                  <tr key={item.stock_ID}>
                    <td>{index + 1}</td>
                    <td>{item.product_Name}</td>
                    <td>{item.category_Name}</td>
                    <td>{item.quantity}</td>
                    <td>{item.last_RestockDate?.split("T")[0]}</td>
                    <td>
                      <Button
                        variant="outline-success"
                        size="sm"
                        className="me-2"
                        onClick={() => handleEdit(item)}
                      >
                        <FaEdit />
                      </Button>
                      <Button
                        variant="outline-danger"
                        size="sm"
                        onClick={() => handleDelete(item.stock_ID)}
                      >
                        <FaTrash />
                      </Button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </Table>
          )}
        </Card.Body>
      </Card>
      <AddEditStockModal
        show={showModal}
        handleClose={() => {
          setShowModal(false);
          setEditStock(null);
        }}
        handleSave={handleSave}
        initial={editStock}
        products={products}
      />
    </Container>
  );
};

export default StockManagement;
