import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import { FaHome, FaBox, FaUsers, FaClipboardList, FaWarehouse, FaTags, FaTruck } from 'react-icons/fa';

const Sidebar = () => {
  const location = useLocation();

  return (
    <div className="sidebar p-3">
      <Link className={`nav-link${location.pathname === '/' ? ' active' : ''}`} to="/"><FaHome /> Dashboard</Link>
      <Link className={`nav-link${location.pathname === '/categories' ? ' active' : ''}`} to="/categories"><FaTags /> Categories</Link>
      <Link className={`nav-link${location.pathname === '/suppliers' ? ' active' : ''}`} to="/suppliers"><FaTruck /> Suppliers</Link>
      <Link className={`nav-link${location.pathname === '/products' ? ' active' : ''}`} to="/products"><FaBox /> Products</Link>
      <Link className={`nav-link${location.pathname === '/stock' ? ' active' : ''}`} to="/stock"><FaWarehouse /> Stock</Link>
      <Link className={`nav-link${location.pathname === '/orders' ? ' active' : ''}`} to="/orders"><FaClipboardList /> Orders</Link>
      <Link className={`nav-link${location.pathname === '/users' ? ' active' : ''}`} to="/users"><FaUsers /> Users</Link>
    </div>
  );
};

export default Sidebar;
