import React, { useState, useEffect } from 'react';
import { Modal, Button, Form } from 'react-bootstrap';

const AddEditStockModal = ({ show, handleClose, handleSave, initial }) => {
  const [form, setForm] = useState({
    product_ID: '',
    stock_Quantity: '',
    last_RestockDate: ''
  });

  useEffect(() => {
    setForm(initial ? {
      product_ID: initial.product_ID || '',
      stock_Quantity: initial.stock_Quantity || '',
      last_RestockDate: initial.last_RestockDate || ''
    } : {
      product_ID: '',
      stock_Quantity: '',
      last_RestockDate: ''
    });
  }, [initial]);

  const onChange = e => setForm({ ...form, [e.target.name]: e.target.value });

  const onSave = () => {
    handleSave(form);
    setForm({
      product_ID: '',
      stock_Quantity: '',
      last_RestockDate: ''
    });
  };

  return (
    <Modal show={show} onHide={handleClose} centered>
      <Modal.Header closeButton>
        <Modal.Title>{initial ? 'Edit' : 'Add'} Stock</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <Form>
          <Form.Group className="mb-2">
            <Form.Label>Product ID</Form.Label>
            <Form.Control name="product_ID" value={form.product_ID} onChange={onChange} />
          </Form.Group>
          <Form.Group className="mb-2">
            <Form.Label>Quantity</Form.Label>
            <Form.Control name="stock_Quantity" type="number" value={form.stock_Quantity} onChange={onChange} />
          </Form.Group>
          <Form.Group className="mb-2">
            <Form.Label>Last Restock Date</Form.Label>
            <Form.Control name="last_RestockDate" type="date" value={form.last_RestockDate} onChange={onChange} />
          </Form.Group>
        </Form>
      </Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={handleClose}>Cancel</Button>
        <Button variant="primary" onClick={onSave}>Save</Button>
      </Modal.Footer>
    </Modal>
  );
};

export default AddEditStockModal;
