import React, { useEffect, useState } from 'react';
import { Table, Button, Modal } from 'react-bootstrap';
import AddEditOrderModal from './AddEditOrderModal';

function PurchaseOrders() {
  const [orders, setOrders] = useState([]);
  const [suppliers, setSuppliers] = useState([]);
  const [products, setProducts] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [editOrder, setEditOrder] = useState(null);

  useEffect(() => {
    fetch('/api/purchaseorders')
      .then(res => res.json())
      .then(data => setOrders(data));
    fetch('/api/suppliers')
      .then(res => res.json())
      .then(data => setSuppliers(data));
    fetch('/api/products')
      .then(res => res.json())
      .then(data => setProducts(data));
  }, []);

  const handleAdd = () => {
    setEditOrder(null);
    setShowModal(true);
  };

  const handleEdit = (order) => {
    setEditOrder(order);
    setShowModal(true);
  };

  const handleDelete = (id) => {
    if (window.confirm('Are you sure you want to delete this order?')) {
      fetch(`/api/purchaseorders/${id}`, { method: 'DELETE' })
        .then(() => setOrders(orders.filter(o => o.purchase_OrderID !== id)));
    }
  };

  const handleSave = (savedOrder) => {
    if (editOrder) {
      // Edit existing
      fetch(`/api/purchaseorders/${editOrder.purchase_OrderID}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(savedOrder)
      })
        .then(res => res.json())
        .then(updated => {
          setOrders(orders.map(o => o.purchase_OrderID === updated.purchase_OrderID ? updated : o));
          setShowModal(false);
        });
    } else {
      // Create new
      fetch('/api/purchaseorders', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(savedOrder)
      })
        .then(res => res.json())
        .then(newOrder => {
          setOrders([...orders, newOrder]);
          setShowModal(false);
        });
    }
  };

  const getSupplierName = (id) => suppliers.find(s => s.supplier_ID === id)?.supplier_Company || 'N/A';
  const getProductName = (id) => products.find(p => p.product_ID === id)?.product_Name || 'N/A';

  return (
    <div className="container mt-5">
      <h2>Purchase Orders</h2>
      <Button variant="primary" onClick={handleAdd}>Add Order</Button>
      <Table striped bordered hover className="mt-3">
        <thead>
          <tr>
            <th>Order ID</th>
            <th>Supplier</th>
            <th>Product</th>
            <th>Quantity</th>
            <th>Order Date</th>
            <th>Unit Price</th>
            <th>Status</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {orders.map(order => (
            <tr key={order.purchase_OrderID}>
              <td>{order.purchase_OrderID}</td>
              <td>{getSupplierName(order.supplier_ID)}</td>
              <td>{getProductName(order.product_ID)}</td>
              <td>{order.quantity_Ordered}</td>
              <td>{order.order_Date}</td>
              <td>{order.unit_Price}</td>
              <td>{order.status}</td>
              <td>
                <Button size="sm" variant="info" onClick={() => handleEdit(order)}>Edit</Button>{' '}
                <Button size="sm" variant="danger" onClick={() => handleDelete(order.purchase_OrderID)}>Delete</Button>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>

      <Modal show={showModal} onHide={() => setShowModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>{editOrder ? 'Edit Order' : 'Add Order'}</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <AddEditOrderModal
            initial={editOrder}
            onSave={handleSave}
            onClose={() => setShowModal(false)}
            suppliers={suppliers}
            products={products}
          />
        </Modal.Body>
      </Modal>
    </div>
  );
}

export default PurchaseOrders;
